$Capable = "C:\Install\Capable.txt"
If (Test-Path -Path $Capable) { 
    # Required
    $winTitle = "Technology Notification"
    $depAttention = "Brightway, Inc."
    $subTitle = "Windows 11 Upgrade Complete!"
    $bodText = "If you have any issues please don't hesitate to reach out to our HelpDesk either by e-mail or by phone.`n`nemail@buisness.com  |  555.55.5555`n`n`Please restart your computer for the upgrade to complete."
    # TitleOnly ImageOnly ImageAndTitle
    $hdrFormat = "ImageOnly" 
    $audSource = "ms-winsoundevent:Notification.Default"

    # Could be used to open the SCCM Software Center right to App Install. Using Software Center Shared Links --- Applications in SCCM Console
    # $SoftwareCenterShortcut= “softwarecenter:SoftwareID=Site_8E25450A-4C7E-4508-B501-B3F0E2C91541/SUM_99d11a78-2df9-48e9-985e-11968f50fbcd”

    # Register AppID
    $regPath = 'HKCU:\SOFTWARE\Microsoft\Windows\CurrentVersion\Notifications\Settings'
    $app = '{1AC14E77-02E7-4E5D-B744-2EB1AE5198B7}\WindowsPowerShell\v1.0\powershell.exe'
    $appID = "{1AC14E77-02E7-4E5D-B744-2EB1AE5198B7}\\WindowsPowerShell\\v1.0\\powershell.exe"

    # Load Namespaces
    $null = [Windows.UI.Notifications.ToastNotificationManager, Windows.UI.Notifications, ContentType = WindowsRuntime]
    $null = [Windows.Data.Xml.Dom.XmlDocument, Windows.Data.Xml.Dom.XmlDocument, ContentType = WindowsRuntime]

    $bas64Image = ""
    # $bas64Image = ""

    # Generate Image from Base64
    If ($bas64Image) {
        $tmpImage = "$env:TEMP\tmpImage.png"
        [byte[]]$Bytes = [convert]::FromBase64String($bas64Image)
        [System.IO.File]::WriteAllBytes($tmpImage, $Bytes)
    }

    # Validate App exists in registry. If so, use ActionCenter.
    if (!(Test-Path -Path "$regPath\$appID")) {
        $null = New-Item -Path "$regPath\$appID" -Force
        $null = New-ItemProperty -Path "$regPath\$appID" -Name 'ShowInActionCenter' -Value 1 -PropertyType 'DWORD'
    }

    # Toast XML
    [xml]$xmlTemplate = @"
<toast scenario="reminder">
    <visual>
    <binding template="ToastGeneric" activationType="protocol">
        <text>$winTitle</text>
        <text placement="attribution">$depAttention</text>        
        <group>
            <subgroup>
                <text hint-style="Title" hint-wrap="true" >$winTitle</text>
            </subgroup>
        </group>
        <group>          
            <subgroup>     
                <text hint-style="SubTitle" hint-wrap="true" >$subTitle</text>
            </subgroup>
        </group>
        <group>
            <subgroup>     
                <text hint-style="Body" hint-wrap="true" >$bodText</text>
            </subgroup>
        </group>
    </binding>
    </visual>

    <actions>
        <action content="Close" activationType="protocol" arguments="" />
    </actions>
    <audio src="$audSource"/>
</toast>
"@

    # ref: https://docs.microsoft.com/en-us/windows/uwp/design/shell/tiles-and-notifications/toast-xml-schema
    # https://docs.microsoft.com/en-us/windows/uwp/design/shell/tiles-and-notifications/toast-schema
    # file:///C:/Users/Demo99/Desktop/practical-reverse-engineering.pdf
    # activationType? = "foreground | background | protocol | system"

    #        <input id="snoozeTime" type="selection" defaultInput="1">
    #        <selection id="1" content="1 minutes" />
    #        <selection id="10" content="10 minutes" />
    #        <selection id="20" content="20 minutes" />
    #        <selection id="30" content="30 minutes" />
    #        <selection id="60" content="1 hour" />
    #        </input>
    # <action content="Later" activationType="protocol" arguments="snooze" hint-inputId="snoozeTime"/>


    # TitleOnly
    If ($hdrFormat -eq "TitleOnly") {
        $xmlTemplate.toast.visual.binding.group[0].subgroup.InnerXml = "<text hint-style=""Title"" hint-wrap=""true"" >$winTitle</text>"
    }

    # ImageOnly
    If ($hdrFormat -eq "ImageOnly") {
        $xmlTemplate.toast.visual.binding.group[0].subgroup.InnerXml = "<image src=""$tmpImage""/>"
    }

    # ImageAndTitle (image must be smaller)
    If ($hdrFormat -eq "ImageAndTitle") {
        $xmlTemplate.toast.visual.binding.group[0].subgroup.InnerXml = "<image src=""$tmpImage""/><text hint-style=""Title"" hint-wrap=""true"" >$winTitle</text>"
    }

    # Load
    $xmlToast = New-Object -TypeName Windows.Data.Xml.Dom.XmlDocument
    $xmlToast.LoadXml($xmlTemplate.OuterXml)

    # Display
    [Windows.UI.Notifications.ToastNotificationManager]::CreateToastNotifier($app).Show($xmlToast)

    # Clear session
    $winTitle = ""
    $depAttention = ""
    $subTitle = ""
    $bodText = ""
    $hdrFormat = "" 
    $audSource = ""
    $objImage = ""
    $objStream = ""
    $regPath = ""
    $app = ""
    $appID = ""
    $hdrFormat = ""
    $TimeSpan = ""
    $app = ""
    $xmlToast = ""
    $xmlTemplate = ""
}